/**
 * 字符串安全转换到整型，转换失败返回0
 */
fun String.safeConvertToInt(): Int {
    return try {
        toInt()
    } catch (e: Exception) {
        0
    }
}

/**
 * 字符串安全转换到长整型，转换失败返回0
 */
fun String.safeConvertToLong(): Long {
    return try {
        toLong()
    } catch (e: Exception) {
        0L
    }
}

/**
 * 字符串安全转换到双精度类型，转换失败返回0
 */
fun String.safeConvertToDouble(): Double {
    return try {
        toDouble()
    } catch (e: Exception) {
        0.0
    }
}

/**
 * 字符串安全转换到短整型类型，转换失败返回0
 */
fun String.safeConvertToShort(): Short {
    return try {
        toShort()
    } catch (e: Exception) {
        0
    }
}


//首字母大写
fun String.upperFirstLetter(): String {
    return if (this.isEmpty()) {
        ""
    } else {
        if (!Character.isLowerCase(this[0])) return this
        return (this[0].code - 32).toChar() + this.substring(1)
    }

}

/**
 * url 字符串解码
 */
fun String.urlEncode(): String {
    val encode: String = URLEncoder.encode(this, "utf-8")
    return encode.replace("%3A", ":").replace("%2F", "/")
}

/**
 * @author 尧
 * String左对齐，废弃的函数，应该使用标准库实现
 * @see [String.padStart]
 */
@Deprecated("should use standard lib's 'padStart(len,ch)' to instead", ReplaceWith("this.padStart(len, ch)"))
fun String.padLeft(len: Int, ch: Char): String = this.padStart(len, ch)

/**
 * @author 尧
 * String右对齐，废弃的函数，应该使用标准库实现
 * @see [String.padEnd]
 */
@Deprecated("should use standard lib's 'padEnd(len,ch)' to instead", ReplaceWith("this.padEnd(len, ch)"))
fun String.padRight(len: Int, ch: Char): String = this.padEnd(len, ch)

@ExperimentalContracts
fun String?.isNotNullOrEmpty(): Boolean {
    contract {
        returns(true) implies (this@isNotNullOrEmpty != null)
    }
    return this != null && !this.trim().equals("null", true) && this.trim().isNotEmpty()
}
